@override Widget build(BuildContext context) { return SafeArea(child: Scaffold(appBar: _buildAppBar(context), body: Padding(padding: EdgeInsets.only(top: 20.v), child: BlocSelector<NotificationBloc, NotificationState, NotificationModel?>(selector: (state) => state.notificationModelObj, builder: (context, notificationModelObj) {return GroupedListView<TodaysectionItemModel, String>(shrinkWrap: true, stickyHeaderBackgroundColor: Colors.transparent, elements: notificationModelObj?.todaysectionItemList ?? [], groupBy: (element) => element.groupBy!, sort: false, groupSeparatorBuilder: (String value) {return Padding(padding: EdgeInsets.only(top: 31.v, bottom: 16.v), child: Column(children: [Row(mainAxisAlignment: MainAxisAlignment.spaceBetween, children: [Text(value, style: CustomTextStyles.bodyMediumGray50002.copyWith(color: appTheme.gray50002)), Text(value, style: CustomTextStyles.bodyMediumGreenA200.copyWith(color: appTheme.greenA200))])]));}, itemBuilder: (context, model) {return TodaysectionItemWidget(model);}, separator: SizedBox(height: 17.v));})))); } 
